<script>
export default {
  data() {
    return {
      loading: ".",
    };
  },
  computed: {
    clue() {
      return this.$store.state.state.clue;
    },
    displayClue() {
      if (this.clue) {
        return this.clue;
      } else {
        return this.loading;
      }
    },
    picked() {
      if (this.clue) {
        return "(" + this.$store.state.state.picked + " picked)";
      } else {
        return "";
      }
    },
  },
  beforeCreate() {
    setInterval(() => {
      if (!this.clue) {
        switch (this.loading) {
          case ".":
            this.loading = "..";
            break;
          case "..":
            this.loading = "...";
            break;
          case "...":
            this.loading = "....";
            break;
          case "....":
            this.loading = ".....";
            break;
          default:
            this.loading = ".";
        }
      }
    }, 500);
  },
};
</script>

